mapper.select:
SELECT a.*, u.name userName
FROM article a LEFT JOIN user u ON a.userId = u.id;
WHERE a.id = #{id}

SELECT a.*, u.name userName FROM article a LEFT JOIN user u ON a.userId = u.id WHERE a.boardId = #{bd} AND u.name=#{st} AND INSTR(a.title, #{st}) > 0 AND INSTR(a.body, #{st}) > 0 ORDER BY
a.id u.name a.id DESC
LIMIT ${start}, ${sz}

SELECT COUNT(a.id)
FROM article a LEFT JOIN user u ON a.userId = u.id
WHERE a.boardId = #{bd}
	AND u.name = #{st}
	AND INSTR(a.title, #{st}) > 0
	AND INSTR(a.body, #{st}) > 0

mapper.update:
UPDATE article
	SET title = #{title}
	body = #{body}
	writeTime = NOW()
WHERE id = #{id}
